package conditions;

public class ForEx {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Printing 1-10");
		for(int i=0;i<=10;i++)
			System.out.println(i);
		System.out.println("Printing no. inn reverse order");
		for(int j=10;j>=0;j--)
			System.out.println(j);
		

	}

}
